import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import axios from 'axios';
import { userActions } from '../_actions';
import Sidebar from '../_components/sidebar/sidebar';
import './Register.scss'

class RegisterPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                firstName: '',
                lastName: '',
                username: '',
                password: ''
            },
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        this.setState({ submitted: true });
        const { user } = this.state;
        const userinfo = {
            firstName: user.firstName,
            lastName: user.lastName,
            username: user.username,
            hash: user.password,
        };
        axios.post('http://localhost:4010', userinfo)
          .then(res => (console.log(res.data)));

        if (user.firstName && user.lastName && user.username && user.password) {
            this.props.register(user);
        }
    }


    render() {
        const { registering  } = this.props;
        const { user, submitted } = this.state;
        console.log(user);
        return (
            <>
            <div className='back'>
                <Sidebar />
                <div  className="col-sm-6 col-sm-offset-4 mt-0 ">
                <div className='title'>
                <h2>Register</h2>
                </div>
                <div className='text'>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !user.firstName ? ' has-error' : '')}>
                        <label htmlFor="firstName">First Name</label>
                        <input type="text" className="form-control" name="firstName" value={user.firstName} onChange={this.handleChange} />
                        {submitted && !user.firstName &&
                            <div className="help-block">First Name is required</div>}
                    </div>
                    <div className='buff'></div>                  
                    <div className={'form-group' + (submitted && !user.lastName ? ' has-error' : '')}>
                        <label htmlFor="lastName">Last Name</label>
                        <input type="text" className="form-control tex" name="lastName" value={user.lastName} onChange={this.handleChange} />
                        {submitted && !user.lastName &&
                            <div className="help-block">Last Name is required</div>}
                    </div>
                    <div className='buff'></div>
                    <div className={'form-group' + (submitted && !user.username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={user.username} onChange={this.handleChange} />
                        {submitted && !user.username &&
                            <div className="help-block">Username is required</div>}
                    </div>
                    <div className='buff'></div>
                    <div className={'form-group' + (submitted && !user.password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={user.password} onChange={this.handleChange} />
                        {submitted && !user.password &&
                            <div className="help-block">Password is required</div>}
                    </div>
                    <div className='buff'></div>
                    <div className="form-group">
                        <button className="btn btn-primary">Register</button>
                        {registering &&
                            <img src="data:image/gif;base64," />}
                        <Link to="/login" className="btn btn-link">Cancel</Link>
                    </div>
                    
                </form>
                </div>
            </div>
            </div>
            </>
        );
    }
}



function mapState(state) {
    const { registering } = state.registration;
    return { registering };
}

const actionCreators = {
    register: userActions.register
}

const connectedRegisterPage = connect(mapState, actionCreators)(RegisterPage);
export { connectedRegisterPage as RegisterPage };
